    private void pilihitemActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_pilihitemActionPerformed
        // TODO add your handling code here:
        String PilihItem = (String)pilihitem.getSelectedItem();
        switch (PilihItem){
            case "Gold Bracelet" :
                harga.setText("300000");
            break;
            case "Silver Bracelet" :
                harga.setText("200000");
            break;
            case "Ring" :
                harga.setText("400000");
            break;
        }
    }//GEN-LAST:event_pilihitemActionPerformed

