    private void countActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_countActionPerformed
        // TODO add your handling code here:
        int Bayar = Integer.parseInt(bayar.getText());
        int Total = Integer.parseInt(total.getText());
        if (Bayar>=Total){
            kembalian.setText(Integer.toString(Bayar-Total));
        }
        else {
            kembalian.setText("Uang Anda Kurang!");
        }
        
    }//GEN-LAST:event_countActionPerformed

    private void exitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_exitActionPerformed
        // TODO add your handling code here:
        Home hm = new Home();
        hm.setVisible(true);
        hm.pack();
        this.dispose();
    }//GEN-LAST:event_exitActionPerformed

